```javascript
async function runStyleAnalysis(siteStyleKey) {
    const resultDiv = document.getElementById('analysis-result');
    resultDiv.innerHTML = '<div class="loading-state" style="padding:12px">分析中...</div>';
    try {
        const data = await apiRequest(CONFIG.ENDPOINTS.STYLE_OPTIMIZER_ANALYZE(siteStyleKey));
        if (data.total_evaluations === 0) {
            resultDiv.innerHTML = '<p style="font-size:13px;color:var(--text-secondary)">評価データがまだありません。画像を生成して評価を蓄積してください。</p>';
            return;
        }
        const axisJa = {color: '色彩', mood: '雰囲気', composition: '構図'}[data.weakest_axis] || data.weakest_axis;
        const trendJa = {improving: '改善傾向', declining: '低下傾向', stable: '安定'}[data.trend] || data.trend;
        const trendColor = {improving: 'var(--success, #22c55e)', declining: 'var(--danger, #ef4444)', stable: 'var(--text-secondary)'}[data.trend];
        resultDiv.innerHTML = `
            <div style="background:var(--bg);border-radius:8px;padding:16px;margin-bottom:12px">
                <div style="font-weight:bold;margin-bottom:8px">弱点分析結果</div>
                <div style="display:grid;grid-template-columns:repeat(3, 1fr);gap:12px;margin-bottom:12px">
                    <div style="text-align:center;padding:8px;border-radius:6px;${data.weakest_axis === 'color' ? 'border:2px solid var(--danger, #ef4444)' : 'border:1px solid var(--border)'}">
                        <div style="font-size:24px;font-weight:bold">${Math.round(data.scores.color)}</div>
                        <div style="font-size:11px;color:var(--text-secondary)">色彩</div>
                    </div>
                    <div style="text-align:center;padding:8px;border-radius:6px;${data.weakest_axis === 'mood' ? 'border:2px solid var(--danger, #ef4444)' : 'border:1px solid var(--border)'}">
                        <div style="font-size:24px;font-weight:bold">${Math.round(data.scores.mood)}</div>
                        <div style="font-size:11px;color:var(--text-secondary)">雰囲気</div>
                    </div>
                    <div style="text-align:center;padding:8px;border-radius:6px;${data.weakest_axis === 'composition' ? 'border:2px solid var(--danger, #ef4444)' : 'border:1px solid var(--border)'}">
                        <div style="font-size:24px;font-weight:bold">${Math.round(data.scores.composition)}</div>
                        <div style="font-size:11px;color:var(--text-secondary)">構図</div>
                    </div>
                </div>
                <div style="font-size:13px">
                    <span>弱点: <strong style="color:var(--danger, #ef4444)">${escapeHtml(axisJa)}</strong></span>
                    <span style="margin-left:12px">トレンド: <span style="color:${trendColor}">${escapeHtml(trendJa)}</span></span>
                    <span style="margin-left:12px">評価数: ${data.total_evaluations}</span>
                </div>
            </div>
        `;
    } catch (e) {
        resultDiv.innerHTML = `<p style="color:var(--danger)">${escapeHtml(e.message)}</p>`;
    }
}

async function generateOptimizationPlan(siteStyleKey) {
    const resultDiv = document.getElementById('plan-result');
    resultDiv.innerHTML = '<div class="loading-state" style="padding:12px">改善プラン生成中...</div>';
    try {
        const data = await apiRequest(CONFIG.ENDPOINTS.STYLE_OPTIMIZER_PLAN(siteStyleKey), { method: 'POST' });
        const axisJa = {color: '色彩', mood: '雰囲気', composition: '構図'}[data.weakness?.axis] || '';
        resultDiv.innerHTML = `
            <div style="background:var(--bg);border-radius:8px;padding:16px;margin-bottom:12px">
                <div style="font-weight:bold;margin-bottom:4px">改善プラン #${data.optimization_id}</div>
                <div style="font-size:12px;color:var(--text-secondary);margin-bottom:12px">対象: ${escapeHtml(axisJa)} (スコア: ${Math.round(data.weakness?.score || 0)})</div>
                ${(data.suggestions || []).map((s, i) => `
                    <div style="padding:10px;background:var(--card-bg, white);border:1px solid var(--border);border-radius:6px;margin-bottom:8px">
                        <div style="font-weight:bold;font-size:13px">${i + 1}. ${escapeHtml(s.action)}</div>
                        <div style="font-size:12px;color:var(--text-secondary);margin-top:4px">${escapeHtml(s.detail)}</div>
                        <div style="font-size:11px;color:var(--success, #22c55e);margin-top:4px">${escapeHtml(s.expected_impact)}</div>
                    </div>
                `).join('')}
                <button class="btn btn-primary btn-sm" style="margin-top:8px"
                        onclick="applyOptimization('${siteStyleKey}', ${data.optimization_id})">
                    この改善を適用
                </button>
            </div>
        `;
        loadOptimizationHistory(siteStyleKey);
    } catch (e) {
        resultDiv.innerHTML = `<p style="color:var(--danger)">${escapeHtml(e.message)}</p>`;
    }
}

async function applyOptimization(siteStyleKey, optimizationId) {
    try {
        const result = await apiRequest(CONFIG.ENDPOINTS.STYLE_OPTIMIZER_APPLY(siteStyleKey, optimizationId), { method: 'POST' });
        showMessage('改善を適用しました', 'success');
        loadOptimizationHistory(siteStyleKey);
    } catch (e) {
        showMessage(`適用エラー: ${e.message}`, 'error');
    }
}

async function loadOptimizationHistory(siteStyleKey) {
    const histDiv = document.getElementById('optimization-history');
    if (!histDiv) return;
    try {
        const data = await apiRequest(CONFIG.ENDPOINTS.STYLE_OPTIMIZER_HISTORY(siteStyleKey));
        if (!data.items || data.items.length === 0) {
            histDiv.innerHTML = '';
            return;
        }
        histDiv.innerHTML = `
            <div style="margin-top:16px">
                <div style="font-weight:bold;font-size:13px;margin-bottom:8px">最適化履歴</div>
                ${data.items.map(item => `
                    <div style="padding:8px;background:var(--bg);border-radius:6px;margin-bottom:6px;font-size:12px">
                        <div style="display:flex;justify-content:space-between">
                            <span>${escapeHtml(item.optimization_type)}</span>
                            <span style="color:${item.applied ? 'var(--success, #22c55e)' : 'var(--text-secondary)'}">${item.applied ? '適用済み' : '未適用'}</span>
                        </div>
                        <div style="color:var(--text-secondary);margin-top:2px">${item.created_at}</div>
                    </div>
                `).join('')}
            </div>
        `;
    } catch (e) {
        console.warn('最適化履歴の読み込み失敗:', e);
    }
}
```

